import React, { Component } from 'react';
import dogBackground from '../../assets/img/tilt_image/dogBackground.jpg';
import 'react-tilt';
import {findDOMNode} from 'react-dom';
const styles = {
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'center',
  alignItems: 'center',
  minHeight: '100vh',
}
class TiltPhaseTwo extends Component {
    constructor(props) {
        super(props)
        this.state = {}    
        this.element = null
        this.width = null
        this.height = null
        this.left = null
        this.top = null
        this.handleMouseEnter = this.handleMouseEnter.bind(this,
             this.props.handleMouseEnter)
        this.handleMouseMove = this.handleMouseMove.bind(this,
             this.props.handleMouseMove)
        this.handleMouseLeave = this.handleMouseLeave.bind(this,
             this.props.handleMouseLeave)
    }

    componentDidMount() {
        this.element = findDOMNode (this)
    }

    handleMouseEnter(e) {
        this.updateElementPosition()
    }

    updateElementPosition(){
        const rect = this.element.getBoundingClientRect()
        this.width = this.element.offsetWidth
        this.height = this.element.offsetHeight
        this.left = rect.left
        this.top = rect.top
        console.log('REKT', rect)
        console.log('OFFSET WIDTH', this.element)
    }
    handleMouseEnter(clientX,clientY) {
      console.log('onMouseEnter', clientX, clientY)
    }
    handleMouseMove(e){
      console.log(
        'onMouseMove',
        e.nativeEvent.clientX, e.nativeEvent.clientY)
    }
    handleMouseLeave(clientX,clientY) {
      console.log('onMouseLeave', clientX, clientY)
    }
  render() {
    return (
      <div style={styles}>
          <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            width: '100%',
            height: '100%',
          }}
          onMouseEnter={this.handleMouseEnter}
          onMouseMove={this.handleMouseMove}
          onMouseLeave={this.handleMouseLeave}
        >
            <h1>Mouse over the photo:</h1>
        </div>
        
        <img src={dogBackground} alt="dogBack" />
      </div>
    )
  }
}
export default TiltPhaseTwo
